mapper.delete:
delete from tbl_resident_contact_service  
where  1=1 

   and ID = #{id,jdbcType=VARCHAR}        


   and CONTACT_IDENTITY = #{contactidentity,jdbcType=VARCHAR}        


   and RECORD_IDENTITY = #{recordidentity,jdbcType=VARCHAR}        


   and SERVICE_ITEM_IDENTITY = #{serviceitemidentity,jdbcType=VARCHAR}        


   and SERVICE_CATEGORY_ID = #{servicecategoryid,jdbcType=VARCHAR}        


   and SERVICE_CATEGORY_NAME = #{servicecategoryname,jdbcType=VARCHAR}        


   and APPLY_STATUS = #{applystatus,jdbcType=SMALLINT}        


   and CONTACT_NUMBER = #{contactnumber,jdbcType=VARCHAR}        


   and TOTAL_COUNT = #{totalcount,jdbcType=INTEGER}        


   and SERVICE_COUNT = #{servicecount,jdbcType=INTEGER}        


   and SERVICE_NAME = #{servicename,jdbcType=VARCHAR}        


   and FIRST_SERVICE_DATE = #{firstservicedate,jdbcType=TIMESTAMP}        


   and IS_FOUNDATION = #{isfoundation,jdbcType=BIT}        


   and CUSTOMER_CATEGORY = #{customercategory,jdbcType=SMALLINT}        


   and IS_APPOINTMENT = #{isappointment,jdbcType=SMALLINT}        


   and IS_ENABLE = #{isenable,jdbcType=BIT}        


   and SERVICE_MESURE = #{servicemesure,jdbcType=SMALLINT}        


   and SERVICE_MESURE_UNIT = #{servicemesureunit,jdbcType=VARCHAR}        


   and SERVICE_PERIOD_IDENTITY = #{serviceperiodidentity,jdbcType=VARCHAR}        


   and SERVICE_CHARGE = #{servicecharge,jdbcType=DECIMAL}        


   and SERVICE_CHARGE_UNIT = #{servicechargeunit,jdbcType=VARCHAR}        


   and SERVICE_CHARGE_FREQUENCY_IDENTITY = #{servicechargefrequencyidentity,jdbcType=VARCHAR}        


   and SERVICE_CHARGE_FREQUENCY_NAME = #{servicechargefrequencyname,jdbcType=VARCHAR}        


   and SERVICE_REWARD = #{servicereward,jdbcType=DECIMAL}        


   and DOCTOR_MESURE = #{doctormesure,jdbcType=SMALLINT}        


   and DOCTOR_MESURE_UNIT = #{doctormesureunit,jdbcType=VARCHAR}        


   and MEDICAL_EQUIPMENT_IDENTITY = #{medicalequipmentidentity,jdbcType=VARCHAR}        


   and MEDICAL_EQUIPMENT_NAME = #{medicalequipmentname,jdbcType=VARCHAR}        


   and DELETE_SIGN = #{deletesign,jdbcType=BIT}        


   and AREA_CODE = #{areacode,jdbcType=VARCHAR}        


   and ORG_ID = #{orgid,jdbcType=VARCHAR}        


   and CREATE_USER = #{createuser,jdbcType=VARCHAR}        


   and CREATE_DATE = #{createdate,jdbcType=TIMESTAMP}        


   and UPDATE_USER = #{updateuser,jdbcType=VARCHAR}        


   and UPDATE_DATE = #{updatedate,jdbcType=TIMESTAMP}
