mapper.select:
select                               

from tbl_resident_contact_service_plan
where DELETE_SIGN = 0

    AND ID = #{id}


    AND SERVICE_CATEGORY_ID = #{servicecategoryid}


    AND SERVICE_CATEGORY_NAME = #{servicecategoryname}


    AND SERVICE_IDENTIFY = #{serviceidentify}


    AND SERVICE_NAME = #{servicename}


    AND DETAIL = #{detail}


    AND RECORD_IDENTIFY = #{recordidentify}


    AND CONTACT_IDENTIFY = #{contactidentify}


    AND SERVICE_NUMBER = #{servicenumber}


    AND CONTACT_NUMBER = #{contactnumber}


    AND EXPECT_SERVICE_DATE = #{expectservicedate}


    AND SERVICE_DATE = #{servicedate}


    AND MEMBER_IDENTITY = #{memberidentity}


    AND MEMBER_NAME = #{membername}


    AND SEX = #{sex}


    AND AGE = #{age}


    AND IDENTITY_NUMBER = #{identitynumber}


    AND RESIDENT_TELPHONE = #{residenttelphone}


    AND RESIDENT_ADDRESS = #{residentaddress}


    AND MEDICAL_TEAM_IDENTITY = #{medicalteamidentity}


    AND MEDICAL_TEAM_NAME = #{medicalteamname}


    AND DOCTOR_IDENTITY = #{doctoridentity}


    AND DOCTOR_NAME = #{doctorname}


    AND DOCTOR_ROLE_IDENTITY = #{doctorroleidentity}


    AND DOCTOR_ROLE_NAME = #{doctorrolename}


    AND IS_APPOINTMENT = #{isappointment}


    AND SERVICE_RATE = #{servicerate}


    AND IS_TRANSFER = #{istransfer}


    AND STATUS = #{status}


    AND START_DATE = #{startdate}


    AND END_DATE = #{enddate}





    AND AREA_CODE = #{areacode}


    AND ORG_ID = #{orgid}


    AND CREATE_USER = #{createuser}


    AND CREATE_DATE = #{createdate}


    AND UPDATE_USER = #{updateuser}


    AND UPDATE_DATE = #{updatedate}

        select                               

        from tbl_resident_contact_service_plan
        where DELETE_SIGN = 0

            AND ID = #{searchParams.id}


            AND SERVICE_CATEGORY_ID = #{searchParams.servicecategoryid}


            AND SERVICE_CATEGORY_NAME = #{searchParams.servicecategoryname}


            AND SERVICE_IDENTIFY = #{searchParams.serviceidentify}


            AND SERVICE_NAME = #{searchParams.servicename}


            AND DETAIL = #{searchParams.detail}


            AND RECORD_IDENTIFY = #{searchParams.recordidentify}


            AND CONTACT_IDENTIFY = #{searchParams.contactidentify}


            AND SERVICE_NUMBER = #{searchParams.servicenumber}


            AND CONTACT_NUMBER = #{searchParams.contactnumber}


            AND EXPECT_SERVICE_DATE = #{searchParams.expectservicedate}


            AND SERVICE_DATE = #{searchParams.servicedate}


            AND MEMBER_IDENTITY = #{searchParams.memberidentity}


            AND MEMBER_NAME = #{searchParams.membername}


            AND SEX = #{searchParams.sex}


            AND AGE = #{searchParams.age}


            AND IDENTITY_NUMBER = #{searchParams.identitynumber}


            AND RESIDENT_TELPHONE = #{searchParams.residenttelphone}


            AND RESIDENT_ADDRESS = #{searchParams.residentaddress}


            AND MEDICAL_TEAM_IDENTITY = #{searchParams.medicalteamidentity}


            AND MEDICAL_TEAM_NAME = #{searchParams.medicalteamname}


            AND DOCTOR_IDENTITY = #{searchParams.doctoridentity}


            AND DOCTOR_NAME = #{searchParams.doctorname}


            AND DOCTOR_ROLE_IDENTITY = #{searchParams.doctorroleidentity}


            AND DOCTOR_ROLE_NAME = #{searchParams.doctorrolename}


            AND IS_APPOINTMENT = #{searchParams.isappointment}


            AND SERVICE_RATE = #{searchParams.servicerate}


            AND IS_TRANSFER = #{searchParams.istransfer}


            AND STATUS = #{searchParams.status}


            AND START_DATE = #{searchParams.startdate}


            AND END_DATE = #{searchParams.enddate}





            AND AREA_CODE = #{searchParams.areacode}


            AND ORG_ID = #{searchParams.orgid}


            AND CREATE_USER = #{searchParams.createuser}


            AND CREATE_DATE = #{searchParams.createdate}


            AND UPDATE_USER = #{searchParams.updateuser}


            AND UPDATE_DATE = #{searchParams.updatedate}


            AND DATE_FORMAT(EXPECT_SERVICE_DATE,'%Y-%m-%d') >= DATE_FORMAT(#{searchParams.beginDate},'%Y-%m-%d')



			AND DATE_FORMAT(EXPECT_SERVICE_DATE,'%Y-%m-%d') <= DATE_FORMAT(#{searchParams.endDate},'%Y-%m-%d')





                AND DATE_FORMAT(EXPECT_SERVICE_DATE,'%Y-%m-%d') >= date_sub(DATE_FORMAT(EXPECT_SERVICE_DATE,'%Y-%m-%d'), interval 1 month)



                AND DATE_FORMAT(EXPECT_SERVICE_DATE,'%Y-%m-%d') >= date_sub(DATE_FORMAT(EXPECT_SERVICE_DATE,'%Y-%m-%d'), interval 3 month)



                AND DATE_FORMAT(EXPECT_SERVICE_DATE,'%Y-%m-%d') >= date_sub(DATE_FORMAT(EXPECT_SERVICE_DATE,'%Y-%m-%d'), interval 6 month)             



                AND DATE_FORMAT(EXPECT_SERVICE_DATE,'%Y-%m-%d') >= date_sub(DATE_FORMAT(EXPECT_SERVICE_DATE,'%Y-%m-%d'), interval 1 year)             




          AND (APPLY_NAME like '%${searchParams.keyWords}%' OR IDENTITY_NUMBER like '%${searchParams.keyWords}%')

mapper.delete:
delete from tbl_resident_contact_service_plan  
where  1=1 

   and ID = #{id,jdbcType=VARCHAR}        


   and SERVICE_CATEGORY_ID = #{servicecategoryid,jdbcType=VARCHAR}        


   and SERVICE_CATEGORY_NAME = #{servicecategoryname,jdbcType=VARCHAR}        


   and SERVICE_IDENTIFY = #{serviceidentify,jdbcType=VARCHAR}        


   and SERVICE_NAME = #{servicename,jdbcType=VARCHAR}        


   and DETAIL = #{detail,jdbcType=VARCHAR}        


   and RECORD_IDENTIFY = #{recordidentify,jdbcType=VARCHAR}        


   and CONTACT_IDENTIFY = #{contactidentify,jdbcType=VARCHAR}        


   and SERVICE_NUMBER = #{servicenumber,jdbcType=VARCHAR}        


   and CONTACT_NUMBER = #{contactnumber,jdbcType=VARCHAR}        


   and EXPECT_SERVICE_DATE = #{expectservicedate,jdbcType=TIMESTAMP}        


   and SERVICE_DATE = #{servicedate,jdbcType=TIMESTAMP}        


   and MEMBER_IDENTITY = #{memberidentity,jdbcType=VARCHAR}        


   and MEMBER_NAME = #{membername,jdbcType=VARCHAR}        


   and SEX = #{sex,jdbcType=SMALLINT}        


   and AGE = #{age,jdbcType=SMALLINT}        


   and IDENTITY_NUMBER = #{identitynumber,jdbcType=VARCHAR}        


   and RESIDENT_TELPHONE = #{residenttelphone,jdbcType=VARCHAR}        


   and RESIDENT_ADDRESS = #{residentaddress,jdbcType=VARCHAR}        


   and MEDICAL_TEAM_IDENTITY = #{medicalteamidentity,jdbcType=VARCHAR}        


   and MEDICAL_TEAM_NAME = #{medicalteamname,jdbcType=VARCHAR}        


   and DOCTOR_IDENTITY = #{doctoridentity,jdbcType=VARCHAR}        


   and DOCTOR_NAME = #{doctorname,jdbcType=VARCHAR}        


   and DOCTOR_ROLE_IDENTITY = #{doctorroleidentity,jdbcType=VARCHAR}        


   and DOCTOR_ROLE_NAME = #{doctorrolename,jdbcType=VARCHAR}        


   and IS_APPOINTMENT = #{isappointment,jdbcType=SMALLINT}        


   and SERVICE_RATE = #{servicerate,jdbcType=SMALLINT}        


   and IS_TRANSFER = #{istransfer,jdbcType=BIT}        


   and STATUS = #{status,jdbcType=SMALLINT}        


