mapper.select:
select                               

from tbl_treatment_records
where 1=1

    AND ID = #{id}


    AND SERVICE_NUMBER = #{servicenumber}


    AND RECORD_IDENTITY = #{recordidentity}


    AND PLAN_IDENTIFY = #{planidentify}


    AND SERVICE_IDENTIFY = #{serviceidentify}


    AND SERVICE_NAME = #{servicename}


    AND DETAIL = #{detail}


    AND CONTACT_IDENTIFY = #{contactidentify}


    AND CONTACT_NUMBER = #{contactnumber}


    AND TEAM_IDENTITY = #{teamidentity}


    AND TEAM_NAME = #{teamname}


    AND DOCTOR_IDENTITY = #{doctoridentity}


    AND DOCTOR_NAME = #{doctorname}


    AND DOCTOR_ROLE_IDENTITY = #{doctorroleidentity}


    AND DOCTOR_ROLE_NAME = #{doctorrolename}


    AND IS_APPOINTMENT = #{isappointment}


    AND SERVICE_DATE = #{servicedate}


    AND NEXT_SERVICE_DATE = #{nextservicedate}


    AND SERVICE_TREATMENT_DOCTOR_IDENTITY = #{servicetreatmentdoctoridentity}


    AND SERVICE_TREATMENT_DOCTOR_NAME = #{servicetreatmentdoctorname}


    AND SERVICE_TREATMENT_DOCTOR_ROLE_IDENTITY = #{servicetreatmentdoctorroleidentity}


    AND SERVICE_TREATMENT_DOCTOR_ROLE_NAME = #{servicetreatmentdoctorrolename}


    AND IS_CONTACT_SERVICE = #{iscontactservice}


    AND CONCLUSION = #{conclusion}


    AND ADVICE = #{advice}


    AND TREATMENT_KEYWORDS = #{treatmentkeywords}


    AND MEMBER_IDENTITY = #{memberidentity}


    AND MEMBER_NAME = #{membername}


    AND SEX = #{sex}


    AND AGE = #{age}


    AND IDENTITY_NUMBER = #{identitynumber}


    AND RESIDENT_TELPHONE = #{residenttelphone}


    AND DOCTOR_RATE = #{doctorrate}


    AND SERVICE_RATE = #{servicerate}


    AND RESIDENT_ADDRESS = #{residentaddress}


    AND DELETE_SIGN = #{deletesign}


    AND AREA_CODE = #{areacode}


    AND ORG_ID = #{orgid}


    AND CREATE_USER = #{createuser}


    AND CREATE_DATE = #{createdate}


    AND UPDATE_USER = #{updateuser}


    AND UPDATE_DATE = #{updatedate}

