mapper.delete:
delete from tbl_treatment_records  
where  1=1 

   and ID = #{id,jdbcType=VARCHAR}        


   and SERVICE_NUMBER = #{servicenumber,jdbcType=VARCHAR}        


   and RECORD_IDENTITY = #{recordidentity,jdbcType=VARCHAR}        


   and PLAN_IDENTIFY = #{planidentify,jdbcType=VARCHAR}        


   and SERVICE_IDENTIFY = #{serviceidentify,jdbcType=VARCHAR}        


   and SERVICE_NAME = #{servicename,jdbcType=VARCHAR}        


   and DETAIL = #{detail,jdbcType=VARCHAR}        


   and CONTACT_IDENTIFY = #{contactidentify,jdbcType=VARCHAR}        


   and CONTACT_NUMBER = #{contactnumber,jdbcType=VARCHAR}        


   and TEAM_IDENTITY = #{teamidentity,jdbcType=VARCHAR}        


   and TEAM_NAME = #{teamname,jdbcType=VARCHAR}        


   and DOCTOR_IDENTITY = #{doctoridentity,jdbcType=VARCHAR}        


   and DOCTOR_NAME = #{doctorname,jdbcType=VARCHAR}        


   and DOCTOR_ROLE_IDENTITY = #{doctorroleidentity,jdbcType=VARCHAR}        


   and DOCTOR_ROLE_NAME = #{doctorrolename,jdbcType=VARCHAR}        


   and IS_APPOINTMENT = #{isappointment,jdbcType=SMALLINT}        


   and SERVICE_DATE = #{servicedate,jdbcType=TIMESTAMP}        


   and NEXT_SERVICE_DATE = #{nextservicedate,jdbcType=TIMESTAMP}        


   and SERVICE_TREATMENT_DOCTOR_IDENTITY = #{servicetreatmentdoctoridentity,jdbcType=VARCHAR}        


   and SERVICE_TREATMENT_DOCTOR_NAME = #{servicetreatmentdoctorname,jdbcType=VARCHAR}        


   and SERVICE_TREATMENT_DOCTOR_ROLE_IDENTITY = #{servicetreatmentdoctorroleidentity,jdbcType=VARCHAR}        


   and SERVICE_TREATMENT_DOCTOR_ROLE_NAME = #{servicetreatmentdoctorrolename,jdbcType=VARCHAR}        


   and IS_CONTACT_SERVICE = #{iscontactservice,jdbcType=BIT}        


   and CONCLUSION = #{conclusion,jdbcType=VARCHAR}        


   and ADVICE = #{advice,jdbcType=VARCHAR}        


   and TREATMENT_KEYWORDS = #{treatmentkeywords,jdbcType=VARCHAR}        


   and MEMBER_IDENTITY = #{memberidentity,jdbcType=VARCHAR}        


   and MEMBER_NAME = #{membername,jdbcType=VARCHAR}        


   and SEX = #{sex,jdbcType=SMALLINT}        


   and AGE = #{age,jdbcType=SMALLINT}        


   and IDENTITY_NUMBER = #{identitynumber,jdbcType=VARCHAR}        


   and RESIDENT_TELPHONE = #{residenttelphone,jdbcType=VARCHAR}        


   and DOCTOR_RATE = #{doctorrate,jdbcType=SMALLINT}        


   and SERVICE_RATE = #{servicerate,jdbcType=SMALLINT}        


   and RESIDENT_ADDRESS = #{residentaddress,jdbcType=VARCHAR}        


   and DELETE_SIGN = #{deletesign,jdbcType=BIT}        


   and AREA_CODE = #{areacode,jdbcType=VARCHAR}        


   and ORG_ID = #{orgid,jdbcType=VARCHAR}        


   and CREATE_USER = #{createuser,jdbcType=VARCHAR}        


   and CREATE_DATE = #{createdate,jdbcType=TIMESTAMP}        


   and UPDATE_USER = #{updateuser,jdbcType=VARCHAR}        


   and UPDATE_DATE = #{updatedate,jdbcType=TIMESTAMP}
